import {useState, useEffect} from "react";
import axios from "axios";

const Character = () => {
    const [characters, setCharacters] = useState([])
    const [favorites, setFavorites] = useState([])

    useEffect(()=>{
        const faves = localStorage.getItem('favorites')
        if(faves != null){
            setFavorites(JSON.parse(faves))
        } else {
            localStorage.setItem('favorites', JSON.stringify(favorites))
        }
    }, [])

    const handleListClick = async (e, key, character)=>{
        e.preventDefault();
        setFavorites([...favorites, {id: key, name: character['name']}])
        localStorage.setItem('favorites', JSON.stringify(favorites));
        console.log(favorites)
    }

    const queryCharacters = async (e)=> {
        try{
            setFavorites(JSON.parse(localStorage.getItem('favorites')))
            axios.get('http://localhost:3001/characters.json', {
                params: { search: e.target.value }
            }).then((response)=> {
                setCharacters(response.data);
            })
        }catch (error){
            console.log(error)
        }
    }
    const characterList = characters.map((character, key)=>{
        return <li key={character['id']} onClick={ e => handleListClick(e, key, character) } style={{cursor: "pointer"}}>{character['name']}</li>
    })
    return (
        <div>
            <input type="text" id="search" name="search"onChange={queryCharacters}/>
            <ul>{characterList}</ul>
        </div>
    );
}

export default Character
