import {useState, useEffect} from "react";
import axios from "axios";

const Character = () => {
    const [characters, setCharacters] = useState([])
    const [favorites, setFavorites] = useState([])
    useEffect(()=>{
        axios.get('http://localhost:3001/characters.json').then((response)=> {
            setCharacters(response.data);
        })
    }, [])

    const handleListClick = async (e, key, character)=>{
        e.preventDefault();
        alert(key)
        setFavorites([...favorites, {id: key, name: character['name']}])
        localStorage.setItem('favorites', JSON.stringify(favorites));
        console.log(favorites)
    }
    const items = characters.map((character, key)=>{
        return <li key={character['id']} onClick={ e => handleListClick(e, key, character['name']) }>{character['name']}</li>
    })
    return (
        <div>
            <ul>{items}</ul>
        </div>
    );
}

export default Character
